package double_pointers.seventy_six;

import java.util.*;

public class Solution {

    public static void main(String[] args) {
        String s = "ADOBECODEBANC";
        String t = "ABC";
        String s1 = "";
        String t1 = "";
        String r = new Solution().minWindow(s,t);
        System.out.println("result: "+r);
        //System.out.println(s.substring(1,3));
        //System.out.println(containStr("a","aa"));
    }

    public String minWindow(String s, String t) {

        if(s==null||t==null)
            return "";
        if(s.length()<t.length())
            return "";
        // number of letters of String s
        Map<Character,Integer> map1 = new HashMap<Character,Integer>();
        // number of letters of String t
        Map<Character,Integer> map2 = new HashMap<Character,Integer>();

        for(char t_:t.toCharArray()) {
            /*if(map2.containsKey(t_)){
                map2.put(t_,map2.get(t_)+1);
            }else{
                map2.put(t_,1);
            }*/
            map2.put(t_,map2.getOrDefault(t_,0)+1);
        }


        int p1 = 0,p2 = 0;
        int min = s.length();
        String min_str = "";

        while(min>=s.length()){
            //System.out.println(p1+" "+p2);
            //System.out.println("sub before mid: "+sub);
            if(containStr(map1,map2)){
                String sub = s.substring(p1,p2);
                //min = sub.length();
                min = p2 - p1;
                min_str = sub;
                break;
            }else{
                if(p2==s.length())
                    return "";
                char c = s.charAt(p2);
                /*if(!map1.containsKey(c)) {
                    map1.put(c, 1);
                } else {
                    map1.put(c, map1.get(c) + 1);
                }*/
                map1.put(c,map1.getOrDefault(c,0)+1);
                p2++;
            }
        }

        //System.out.println("mid");

        while(p2<=s.length()){
            if(containStr(map1,map2)){
                if(p2-p1<min){
                    String sub = s.substring(p1,p2);
                    //min = sub.length();
                    min = p2 - p1;
                    min_str = sub;
                }
            }else{
                p2++;
                if(p2>s.length())
                    break;

                char c = s.charAt(p2-1);
                //System.out.println("next char: "+c);
                /*if(map1.containsKey(c))
                    map1.put(c,map1.get(c)+1);
                else
                    map1.put(c,1);*/
                map1.put(c, map1.getOrDefault(c,0)+1);


            }

            char c = s.charAt(p1);
            map1.put(c,map1.get(c)-1);
            //if(map1.get(c)==0)
            //    map1.remove(c);
            p1++;

            //System.out.println("---------------------");

        }

        return min_str;
    }

    // checking if map1 contains map2
    static boolean containStr(Map<Character,Integer> map1,Map<Character,Integer> map2){

        /*for(char p:map2.keySet()){
            if(!map1.containsKey(p))
                return false;
            if(map1.get(p)<map2.get(p))
                return false;
        }
        return true;*/

        // iterate a map, use iterator and map.engry
        Iterator<Map.Entry<Character, Integer>> iterator = map2.entrySet().iterator();
        while(iterator.hasNext()){
            Map.Entry entry = (Map.Entry) iterator.next();
            Character k = (Character) entry.getKey();
            Integer v = (Integer) entry.getValue();
            /*if(!map1.containsKey(k))
                return false;
            if(map1.get(k)<v)
                return false;*/
            if(map1.getOrDefault(k,0)<v)
                return false;
        }

        return true;
    }

}
