    private int steps;
    public int shortestBridge(int[][] A) {
        ArrayList<Integer[]> island1 = new ArrayList<>();
        ArrayList<Integer[]> island2 = new ArrayList<>();

        for(int i = 0;i<A.length;i++){
            for(int j = 0;j<A[0].length;j++){
                if(A[i][j] == 1){
                    if(island1.isEmpty())
                        find_island(A,3,island1,i,j,i,j);
                    else
                        find_island(A,4,island2,i,j,i,j);
                }
            }
        }
        //System.out.println(island1.size());
        //System.out.println(island2.size());

        reach_theotherland(A,4,3,island2);

        return steps;
    }

    public void reach_theotherland(int [][] map, int from,int to,List<Integer[]> island){
        ArrayList<Integer[]> list = new ArrayList<Integer[]>();
        for(Integer[] grid:island) {
            int X = grid[0];
            int Y = grid[1];
            int[][] direction = {{X - 1, Y}, {X, Y - 1}, {X, Y + 1}, {X + 1, Y}};
            for (int[] next : direction) {
                int X_ = next[0];
                int Y_ = next[1];

                if(X_<0||X_>(map.length-1)||Y_<0||Y_>(map[0].length-1)||map[X_][Y_]==from)
                    continue;
                if(map[X_][Y_]==to){
                    return;
                }
                Integer [] element = {X_,Y_};
                list.add(element);
                map [X_][Y_] = from;
            }
        }
        steps++;
        reach_theotherland(map,from,to,list);
    }

    public void find_island(int [][] world,int tag, List<Integer[]> queue, int origin_X, int origin_Y, int x, int y){
        world[x][y] = tag;
        Integer [] addition = {x, y};
        queue.add(addition);
        //System.out.println("added into queue: "+Arrays.deepToString(addition));
        // up, left, right, down
        int [][] direction = {{x-1,y},{x,y-1},{x,y+1},{x+1,y}};

        for(int[] next:direction){
            int X = next[0];
            int Y = next[1];
            if(X<0||X>(world.length-1)||Y<0||Y>(world[0].length-1))
                continue;
            if(world[X][Y]==1)
                find_island(world,tag,queue,origin_X,origin_Y,X,Y);
        }
    }



