    public int shortestBridge(int[][] A) {
        Queue<Integer[]> island1 = new LinkedList<>();
        // {{x-1,y},{x,y+1},{x+1,y},{x,y-1}};
        int [] direct = {-1,0,1,0,-1};
        boolean loop = true;
        for(int i = 0;i<A.length&&loop;i++){
            for(int j = 0;j<A[0].length;j++) {
                if (A[i][j] == 1) {
                    find_island(A,direct,2, island1, i, j);
                    loop = false;
                    break;
                }
            }
        }
        return reach_theotherland(A,direct,2,1,island1,0);
    }

    public int reach_theotherland(int [][] map, int [] direct,int from,int to,Queue<Integer[]> island, int step){
        int qSize = island.size();

        while(qSize-->0) {
            Integer[] grid = island.poll();
            int X = grid[0];
            int Y = grid[1];
            for (int i = 0;i<4;i++) {
                int X_ = X + direct [i];
                int Y_ = Y + direct [i+1];
                if(X_>=0&&X_<map.length&&Y_>=0&&Y_<map[0].length&&map[X_][Y_]!=from){
                    if(map[X_][Y_]==to)
                        return step;
                    Integer [] element = {X_,Y_};
                    island.add(element);
                    map [X_][Y_] = from;
                }
            }
        }
        return reach_theotherland(map,direct,from,to,island,step+1);
    }

    public void find_island(int [][] world,int [] direct,int tag, Queue<Integer[]> list, int x, int y){
        world[x][y] = tag;
        Integer [] addition = {x, y};
        list.add(addition);
        for(int i = 0;i<4;i++){
            int X = x + direct[i];
            int Y = y + direct[i+1];
            if(X<0||X>(world.length-1)||Y<0||Y>(world[0].length-1))
                continue;
            if(world[X][Y]==1)
                find_island(world,direct,tag,list,X,Y);
        }
    }



